import { useState, useRef, useEffect } from 'react'
import { useInView } from 'framer-motion'
import './index.css'
import Hero from './components/Hero'
import ShiftInFocus from './components/ShiftInFocus'
import IntroducingSIA from './components/IntroducingSIA'
import PlaysOutCards from './components/PlaysOutCards'
import StickyCountdownFooter from './components/StickyCountdownFooter'
import LearningByDoing from './components/LearningByDoing'
import FeatureCardsGrid from './components/FeatureCardsGrid'
import ProgramBuiltOnExperience from './components/ProgramBuiltOnExperience'
import BackedByPeople from './components/BackedByPeople'
import ReviewsCarousel from './components/ReviewsCarousel'
import TrustedByLogos from './components/TrustedByLogos'
import DarkZone from './components/DarkZone'
import FAQSection from './components/FAQSection'
import ReadyNextStep from './components/ReadyNextStep'
import Footer from './components/Footer'

export default function App() {
  const [activated, setActivated] = useState(false)
  const activationRef = useRef<HTMLDivElement>(null)
  const inView = useInView(activationRef, { margin: '0px 0px -100px 0px' })

  // Once it enters view, keep it activated
  useEffect(() => {
    if (inView && !activated) setActivated(true)
  }, [inView, activated])

  return (
    <div className="bg-white" style={{ overflowX: 'clip' }}>
      <main className="max-w-[428px] mx-auto bg-white shadow-2xl relative" style={{ overflowX: 'clip' }}>
        {/* S1 — Hero */}
        <Hero />

        {/* S2 — A Shift in Focus */}
        <ShiftInFocus />

        {/* S3 — Introducing SIA Angel Hub */}
        <IntroducingSIA />

        {/* S4 — Cards "Here's How That Plays Out" (Accordion) */}
        <PlaysOutCards />

        {/* S6 — Welcome to Learning by Doing */}
        <LearningByDoing />

        {/* Activation Point for Sticky Footer - Absolute to avoid white line */}
        <div ref={activationRef} className="absolute h-0 w-0 pointer-events-none" />

        {/* S7 — Feature Cards Grid (Modal) */}
        <FeatureCardsGrid />

        {/* S8 — A Program Built on Experience */}
        <ProgramBuiltOnExperience />

        {/* S9 — Backed by People */}
        <BackedByPeople />

        {/* S10 — Reviews Carousel */}
        <ReviewsCarousel />

        {/* S11 — Trusted By */}
        <TrustedByLogos />

        {/* S12-14 — Dark Zone (Become + Cards + CTA) */}
        <DarkZone />

        {/* S15 — FAQ */}
        <FAQSection />

        {/* S16 — Ready for the Next Step */}
        <ReadyNextStep />

        {/* Sticky countdown — now sitting before official footer */}
        <StickyCountdownFooter activated={activated} />

        {/* S17 — Footer */}
        <Footer />
      </main>
    </div>
  )
}

import React, { useState, useRef } from 'react'
import { motion, AnimatePresence, useInView } from 'framer-motion'

const CARDS = [
  {
    image: '/assets_mobile/investor_card_1.png',
    title: 'You see how investment decisions are formed',
    subtitle: '(not just chosen)',
    preview: "You'll recognize how real decisions come together: ho...",
    body: "You'll recognize how real decisions come together: how seasoned investors frame uncertainty, debate tradeoffs, and commit capital under pressure. You\'re not watching from outside the room — you\'re in it.",
  },
  {
    image: '/assets_mobile/investor_card_2.png',
    title: 'You stop confusing great businesses with great investments',
    subtitle: '',
    preview: "You'll be able to tell when a strong company is not nece...",
    body: "You develop the critical eye to separate a compelling pitch from a fundable business. Pattern recognition built through dozens of real evaluations — not textbooks, not simulations.",
  },
  {
    image: '/assets_mobile/investor_card_3.png',
    title: 'You become an investor.',
    subtitle: "You don't just learn investing.",
    preview: "Not by watching, but by deciding. You'll commit when it...",
    body: "By the end, you\'ve co-invested in a real startup alongside your cohort. An actual commitment, with real capital. Not a case study. Not a mock deal. A decision you made and own.",
  },
]

function InvestorCard({ card, index }: { card: typeof CARDS[0]; index: number }) {
  const [open, setOpen] = useState(false)
  const ref = useRef<HTMLDivElement>(null)
  const inView = useInView(ref, { once: true, margin: '-40px' })

  return (
    <motion.div
      ref={ref}
      layout
      className="overflow-hidden rounded-2xl"
      style={{ background: '#1a1c25' }}
      initial={{ opacity: 0, y: 20 }}
      animate={inView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.45, delay: index * 0.1 }}
    >
      {/* Top: photo + title block */}
      <div className="flex gap-3 p-3">
        <img
          src={card.image}
          alt=""
          className="rounded-xl object-cover shrink-0"
          style={{ width: 110, height: 110 }}
          loading="lazy"
        />
        <div className="flex flex-col justify-center pt-1">
          {index === 2 ? (
            <>
              <p className="leading-tight mb-1" style={{ fontSize: '1.25rem', color: 'rgba(255,255,255,0.4)', fontFamily: 'DM Sans', fontWeight: 100 }}>
                {card.subtitle}
              </p>
              <p className="text-white leading-tight" style={{ fontSize: '1.45rem', fontFamily: 'DM Sans', fontWeight: 400 }}>
                {card.title}
              </p>
            </>
          ) : (
            <>
              <p className="text-white leading-tight mb-0.5" style={{ fontSize: '1.35rem', fontFamily: 'DM Sans', fontWeight: 400 }}>
                {card.title}
              </p>
              {card.subtitle && (
                <p className="leading-tight" style={{ color: 'rgba(255,255,255,0.5)', fontSize: '1.35rem', fontFamily: 'DM Sans', fontWeight: 100 }}>
                  {card.subtitle}
                </p>
              )}
            </>
          )}
        </div>
      </div>

      {/* Bottom: description preview + expand button */}
      <div className="flex items-center gap-2 px-4 pb-4">
        <p
          className="font-avenir-regular text-[13px] flex-1"
          style={{
            color: 'rgba(255,255,255,0.6)',
            overflow: 'hidden',
            display: '-webkit-box',
            WebkitLineClamp: 1,
            WebkitBoxOrient: 'vertical',
          }}
        >
          {card.preview}
        </p>
        <button
          onClick={() => setOpen(!open)}
          className="shrink-0 flex items-center justify-center font-avenir-heavy"
          style={{ background: 'transparent', color: '#fbd979', fontSize: '1.2rem' }}
          aria-label={open ? 'Collapse' : 'Expand'}
        >
          <motion.span
            animate={{ rotate: open ? 45 : 0 }}
            transition={{ duration: 0.25 }}
            style={{ lineHeight: 1 }}
          >
            +
          </motion.span>
        </button>
      </div>

      {/* Expanded body */}
      <AnimatePresence initial={false}>
        {open && (
          <motion.div
            key="body"
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.35, ease: 'easeInOut' }}
            className="overflow-hidden"
          >
            <p
              className="font-avenir-regular text-sm leading-relaxed px-3 pb-4"
              style={{ color: 'rgba(255,255,255,0.75)' }}
            >
              {card.body}
            </p>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  )
}

export default function InvestorCardStack({ triggerRef }: { triggerRef: React.RefObject<HTMLDivElement | null> }) {
  return (
    <section className="px-4 pb-4 pt-0">
      <div className="flex flex-col gap-3">
        {CARDS.map((card, i) => (
          <React.Fragment key={i}>
            {/* Trigger right before second card */}
            {i === 2 && <div ref={triggerRef} style={{ height: 1, marginBottom: -1 }} />}
            <InvestorCard card={card} index={i} />
          </React.Fragment>
        ))}
      </div>
    </section>
  )
}

import { useState, useEffect, useRef } from 'react'
import { motion, useInView } from 'framer-motion'
import type { PanInfo } from 'framer-motion'

const REVIEWS = [
  {
    quote: '\u201cIt didn\u2019t just teach me investing. It changed how I decide.\u201d',
    body: "What an amazing program! I joined it 'knowing' what the discussions would be about\u2026 I was wrong. The depth of interaction and the practical approach completely transformed how I evaluate opportunities.",
    name: 'Matteo Costa',
    role: 'Business Administrator | Angel Investor',
    avatar: '/assets_mobile/investors (1).png',
  },
  {
    quote: '\u201cDeep learning comes from real-life context.\u201d',
    body: 'There is no better way of learning what you read in textbooks and watch in videos than by applying it in real time with real startups and real capital.',
    name: 'Matteo Costa',
    role: 'Business Administrator | Angel Investor',
    avatar: '/assets_mobile/investors (2).png',
  },
  {
    quote: '\u201cA safe space for you to broaden your knowledge in industries.\u201d',
    body: 'One exciting thing about the program is that it creates a safe space for you to broaden your knowledge across sectors without the usual barriers of entry.',
    name: 'Joao Paulo Diogo',
    role: 'Business Administrator | Angel Investor',
    avatar: '/assets_mobile/investors (3).png',
  },
]

const CARD_GAP = 12
const CARD_PEEK = 40   // how much of the next card is visible
const CARD_LEFT_PAD = 16

export default function ReviewsCarousel() {
  const [current, setCurrent] = useState(0)
  const [expanded, setExpanded] = useState<number | null>(null)
  const [cardWidth, setCardWidth] = useState(300)
  const containerRef = useRef<HTMLDivElement>(null)
  const ref = useRef(null)
  const inView = useInView(ref, { once: true, margin: '-80px' })

  useEffect(() => {
    const measure = () => {
      if (containerRef.current) {
        setCardWidth(containerRef.current.offsetWidth - CARD_LEFT_PAD - CARD_PEEK)
      }
    }
    measure()
    window.addEventListener('resize', measure)
    return () => window.removeEventListener('resize', measure)
  }, [])

  // Auto-slide every 4 seconds, paused if a card is expanded
  useEffect(() => {
    if (!inView || expanded !== null) return

    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % REVIEWS.length)
    }, 4000)

    return () => clearInterval(timer)
  }, [inView, current, expanded])

  // Close expanded card when moving to a different slide
  useEffect(() => {
    setExpanded(null)
  }, [current])

  const handleDragEnd = (_: unknown, info: PanInfo) => {
    if (info.offset.x < -50) {
      setCurrent((c) => (c + 1) % REVIEWS.length)
    } else if (info.offset.x > 50) {
      setCurrent((c) => (c - 1 + REVIEWS.length) % REVIEWS.length)
    }
  }

  return (
    <section
      ref={ref}
      className="pt-0 pb-4"
      style={{ background: 'linear-gradient(to bottom, #F5F0E8 0%, #F5F0E8 50%, #f4f7ec 100%)' }}
    >
      {/* Carousel track */}
      <motion.div
        ref={containerRef}
        style={{ overflow: 'hidden' }}
        initial={{ opacity: 0, y: 12 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ duration: 0.5 }}
      >
        <motion.div
          className="flex"
          style={{ paddingLeft: CARD_LEFT_PAD }}
          animate={{ 
            x: -(current * (cardWidth + CARD_GAP)) + (current / (REVIEWS.length - 1)) * (CARD_PEEK - CARD_LEFT_PAD)
          }}
          transition={{ type: 'spring', stiffness: 300, damping: 30 }}
          drag="x"
          dragConstraints={{
            left: -((REVIEWS.length - 1) * (cardWidth + CARD_GAP)) + (CARD_PEEK - CARD_LEFT_PAD),
            right: 0,
          }}
          onDragEnd={handleDragEnd}
        >
          {REVIEWS.map((review, i) => {
            const isExpanded = expanded === i
            return (
              <div
                key={i}
                style={{ width: cardWidth, flexShrink: 0, marginRight: CARD_GAP }}
              >
                <div
                  className="rounded-2xl bg-white p-4"
                  style={{ border: '1px solid #E5E7EB' }}
                >
                  {/* Quote */}
                  <p
                    className="font-avenir-heavy text-sm italic leading-snug mb-2"
                    style={{ color: '#1B2A4A' }}
                  >
                    {review.quote}
                  </p>

                  {/* Body — Animated expansion */}
                  <motion.div
                    className="overflow-hidden"
                    initial={false}
                    animate={{ height: isExpanded ? 'auto' : 40 }}
                    transition={{ duration: 0.35, ease: 'easeInOut' }}
                  >
                    <p
                      className="font-avenir-regular text-xs leading-relaxed mb-3"
                      style={{ color: '#6B7280' }}
                    >
                      {review.body}
                    </p>
                  </motion.div>

                  {/* Footer: avatar + name/role + chevron */}
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      <img
                        src={review.avatar}
                        alt={review.name}
                        className="rounded-full object-cover shrink-0"
                        style={{ width: 32, height: 32 }}
                        loading="lazy"
                      />
                      <div>
                        <p className="font-avenir-medium text-xs" style={{ color: '#1B2A4A' }}>
                          &mdash; {review.name}
                        </p>
                        <p className="font-avenir-light text-xs" style={{ color: '#6B7280' }}>
                          {review.role}
                        </p>
                      </div>
                    </div>

                    {/* Chevron expand button */}
                    <button
                      onClick={() => setExpanded(isExpanded ? null : i)}
                      className="shrink-0 flex items-center justify-center"
                      style={{ color: '#9CA3AF', padding: 4 }}
                      aria-label={isExpanded ? 'Collapse review' : 'Expand review'}
                    >
                      <motion.svg
                        width="16"
                        height="16"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2.5"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        animate={{ rotate: isExpanded ? 180 : 0 }}
                        transition={{ duration: 0.25 }}
                      >
                        <polyline points="6 9 12 15 18 9" />
                      </motion.svg>
                    </button>
                  </div>
                </div>
              </div>
            )
          })}
        </motion.div>
      </motion.div>

      {/* Dot indicators — circle outlines, active filled yellow */}
      <div className="flex justify-center gap-2 mt-5">
        {REVIEWS.map((_, i) => (
          <button
            key={i}
            onClick={() => setCurrent(i)}
            className="rounded-full transition-all duration-200"
            style={{
              width: 8,
              height: 8,
              background: i === current ? '#FBD979' : '#D1D5DB',
              border: `1px solid ${i === current ? '#FBD979' : '#D1D5DB'}`,
            }}
            aria-label={`Go to review ${i + 1}`}
          />
        ))}
      </div>
    </section>
  )
}

import { useState, useEffect } from 'react'

interface CountdownValues {
  days: number
  hours: number
  minutes: number
  seconds: number
  isExpired: boolean
}

export function useCountdown(targetDate: string): CountdownValues {
  const calculateTimeLeft = (): CountdownValues => {
    const target = new Date(targetDate).getTime()
    const now = Date.now()
    const diff = target - now

    if (diff <= 0) {
      return { days: 0, hours: 0, minutes: 0, seconds: 0, isExpired: true }
    }

    return {
      days: Math.floor(diff / (1000 * 60 * 60 * 24)),
      hours: Math.floor((diff / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((diff / 1000 / 60) % 60),
      seconds: Math.floor((diff / 1000) % 60),
      isExpired: false,
    }
  }

  const [timeLeft, setTimeLeft] = useState<CountdownValues>(calculateTimeLeft)

  useEffect(() => {
    const interval = setInterval(() => {
      setTimeLeft(calculateTimeLeft())
    }, 1000)
    return () => clearInterval(interval)
  }, [targetDate])

  return timeLeft
}
